import React, { useState } from 'react';
import { 
  StyleSheet, 
  View, 
  Text, 
  TouchableOpacity, 
  ActivityIndicator, 
  Image 
} from 'react-native';
import * as ImagePicker from 'expo-image-picker';
import { Video } from 'expo-av';

export default function HomeScreen({ navigation }) {
  const [videoUri, setVideoUri] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  // Select video from device library
  const pickVideo = async () => {
  if (Platform.OS === 'web') {
    // Web platform - use HTML5 file input
    const input = document.createElement('input');
    input.type = 'file';
    input.accept = 'video/*';
    
    input.onchange = (event) => {
      const file = event.target.files[0];
      if (file) {
        const videoUrl = URL.createObjectURL(file);
        // Pass both the blob URL and the File object
        navigation.navigate('Editor', { 
          videoUri: videoUrl,
          selectedFile: file // Pass the File object for upload
        });
      }
    };
    
    input.click();
  } else {
    // Native platform - use expo-image-picker
    const permissionResult = await ImagePicker.requestMediaLibraryPermissionsAsync();
    
    if (permissionResult.granted === false) {
      Alert.alert('Error', 'Permission to access media library is required!');
      return;
    }

    const result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Videos,
      allowsEditing: false,
      quality: 1,
    });

    if (!result.canceled) {
      navigation.navigate('Editor', { videoUri: result.assets[0].uri });
    }
  }
};

  // Navigate to editor with selected video
  const goToEditor = () => {
    if (videoUri) {
      navigation.navigate('Editor', { videoUri });
    } else {
      alert('Please select a video first.');
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Video Editor</Text>
      <Text style={styles.subtitle}>Upload a video to begin editing</Text>
      
      <TouchableOpacity 
        style={styles.uploadButton} 
        onPress={pickVideo}
        disabled={isLoading}
      >
        {isLoading ? (
          <ActivityIndicator size="small" color="#ffffff" />
        ) : (
          <Text style={styles.buttonText}>Select Video</Text>
        )}
      </TouchableOpacity>
      
      {videoUri ? (
        <View style={styles.previewContainer}>
          <Text style={styles.previewText}>Preview:</Text>
          <Video
            source={{ uri: videoUri }}
            rate={1.0}
            volume={1.0}
            isMuted={false}
            resizeMode="cover"
            shouldPlay={false}
            isLooping={true}
            style={styles.videoPreview}
            useNativeControls
          />
          
          <TouchableOpacity 
            style={styles.editButton} 
            onPress={goToEditor}
          >
            <Text style={styles.buttonText}>Edit Video</Text>
          </TouchableOpacity>
        </View>
      ) : (
        <View style={styles.placeholderContainer}>
          <Text style={styles.placeholderText}>No video selected</Text>
        </View>
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    padding: 20,
  },
  title: {
    fontSize: 28,
    fontWeight: 'bold',
    marginBottom: 8,
    color: '#333',
  },
  subtitle: {
    fontSize: 16,
    color: '#666',
    marginBottom: 24,
  },
  uploadButton: {
    backgroundColor: '#4A90E2',
    padding: 15,
    borderRadius: 8,
    alignItems: 'center',
    marginBottom: 24,
  },
  buttonText: {
    color: '#ffffff',
    fontWeight: 'bold',
    fontSize: 16,
  },
  previewContainer: {
    flex: 1,
    marginBottom: 20,
  },
  previewText: {
    fontSize: 18,
    fontWeight: '600',
    marginBottom: 12,
    color: '#333',
  },
  videoPreview: {
    width: '100%',
    height: 250,
    borderRadius: 8,
    backgroundColor: '#000',
    marginBottom: 20,
  },
  editButton: {
    backgroundColor: '#5CB85C',
    padding: 15,
    borderRadius: 8,
    alignItems: 'center',
  },
  placeholderContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#f5f5f5',
    borderRadius: 8,
    borderWidth: 1,
    borderColor: '#ddd',
    borderStyle: 'dashed',
  },
  placeholderText: {
    color: '#999',
    fontSize: 16,
  },
});
