import { API_ENDPOINTS } from './config';
import { Platform } from 'react-native';

// Platform-specific imports
let FileSystem;
if (Platform.OS !== 'web') {
  FileSystem = require('expo-file-system');
}

// Upload video with overlay metadata
export const uploadVideo = async (videoSource, overlays = []) => {
  try {
    const formData = new FormData();
    
    if (Platform.OS === 'web') {
      // Web platform handling
      if (videoSource instanceof File) {
        // Direct File object
        formData.append('video', videoSource);
      } else if (typeof videoSource === 'string' && videoSource.startsWith('blob:')) {
        // Blob URL - fetch and convert to blob
        const response = await fetch(videoSource);
        const blob = await response.blob();
        formData.append('video', blob, 'video.mp4');
      } else {
        throw new Error('Invalid video source for web platform');
      }
    } else {
      // Native platform handling
      if (!FileSystem) {
        throw new Error('FileSystem not available');
      }
      
      // Check if file exists
      const fileInfo = await FileSystem.getInfoAsync(videoSource);
      if (!fileInfo.exists) {
        throw new Error('Video file does not exist');
      }
      
      // Get filename from URI
      const fileName = videoSource.split('/').pop() || 'video.mp4';
      
      // Create file object for form data
      formData.append('video', {
        uri: videoSource,
        name: fileName,
        type: 'video/mp4',
      });
    }
    
    // Append overlays as JSON string
    formData.append('overlays_json', JSON.stringify(overlays));
    
    // Send request with proper headers
    const response = await fetch(API_ENDPOINTS.UPLOAD, {
      method: 'POST',
      body: formData,
      headers: {
        'Accept': 'application/json',
        // Don't set Content-Type for FormData - browser/RN will set it automatically with boundary
      },
    });
    
    if (!response.ok) {
      let errorMessage = `Upload failed with status: ${response.status}`;
      try {
        const errorData = await response.json();
        errorMessage = errorData.detail || errorData.message || errorMessage;
      } catch (e) {
        const errorText = await response.text();
        errorMessage = errorText || errorMessage;
      }
      throw new Error(errorMessage);
    }
    
    const result = await response.json();
    console.log('Upload successful:', result);
    return result;
    
  } catch (error) {
    console.error('Error uploading video:', error);
    throw new Error(`Upload failed: ${error.message}`);
  }
};

// Check job status with enhanced error handling
export const checkJobStatus = async (jobId) => {
  try {
    if (!jobId) {
      throw new Error('Job ID is required');
    }
    
    const response = await fetch(API_ENDPOINTS.STATUS(jobId), {
      method: 'GET',
      headers: {
        'Accept': 'application/json',
      },
    });
    
    if (!response.ok) {
      let errorMessage = `Status check failed with status: ${response.status}`;
      try {
        const errorData = await response.json();
        errorMessage = errorData.detail || errorData.message || errorMessage;
      } catch (e) {
        const errorText = await response.text();
        errorMessage = errorText || errorMessage;
      }
      throw new Error(errorMessage);
    }
    
    const result = await response.json();
    console.log('Status check result:', result);
    return result;
    
  } catch (error) {
    console.error('Error checking job status:', error);
    throw new Error(`Status check failed: ${error.message}`);
  }
};

// Download rendered video with platform-specific handling
export const downloadVideo = async (jobId, destinationUri = null) => {
  try {
    if (!jobId) {
      throw new Error('Job ID is required');
    }
    
    if (Platform.OS === 'web') {
      // Web platform - trigger download
      const response = await fetch(API_ENDPOINTS.RESULT(jobId));
      
      if (!response.ok) {
        throw new Error(`Download failed with status: ${response.status}`);
      }
      
      // Create download link
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `processed_video_${jobId}.mp4`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);
      
      return { uri: url, status: 'completed' };
      
    } else {
      // Native platform
      if (!FileSystem) {
        throw new Error('FileSystem not available');
      }
      
      // Generate destination URI if not provided
      const finalDestinationUri = destinationUri || 
        `${FileSystem.documentDirectory}processed_video_${jobId}.mp4`;
      
      const downloadResumable = FileSystem.createDownloadResumable(
        API_ENDPOINTS.RESULT(jobId),
        finalDestinationUri,
        {
          headers: {
            'Accept': 'video/mp4',
          },
        }
      );
      
      const result = await downloadResumable.downloadAsync();
      console.log('Download completed:', result);
      return result;
    }
    
  } catch (error) {
    console.error('Error downloading video:', error);
    throw new Error(`Download failed: ${error.message}`);
  }
};
